import { useState, useEffect } from "react"

export default function Navbar({addedClassList}){

    const [user, setUser] = useState({})
    const [view, setView] = useState(true)
    
    useEffect(() => {
        setUser({})
        //setUser(JSON.parse(localStorage.getItem("user")))
    }, [])

    return (
        <div className = {`flex flex-row w-full` + " " + addedClassList}>

            <div>
                <a href = "/">
                    <i class="fa-solid fa-ranking-star text-5xl mb-3 text-yellow-200 hover:text-white transition-all duration-750"></i>
                </a>
            </div>
            
            <div className = "ml-auto">

              <div className = "flex flex-col">
                <a className = "bg-black border-b-2 border-gray-800 hover:border-gray-850 transition-all duration-500 px-2 py-2"
                onClick={() => {setView(!view)}}
                >Your Profile</a>

                <div id = "otherAccountActions" className = {`${view ? "max-h-[1000px]" : "max-h-0"} transition-all duration-500 ease-in`}>
                    <a className = "bg-black border-b-2 border-gray-800 hover:border-gray-850 transition-all duration-500 my-2 px-2 py-2">Logout</a>
                </div>

              </div>

            </div>

        </div>
    )
}
