import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Join() {
  const api_link = process.env.API_LINK;
  const navigate = useNavigate();

  async function login() {
    const body = {
      username: document.getElementById("loginUsername").value,
      password: document.getElementById("loginPass").value,
    };
    console.log(body);
    const { data } = await axios.post(
      process.env.DBAPI_URI + "/users/sign-in",
      body
    );
    if (data.error) {
      document.getElementById("loginErr").innerHTML = data.error;
    } else {
      //Set in cache then
      navigate("/Rank");
    }
  }

  async function create() {
    const pass = document.getElementById("joinPass").value;
    const error = document.getElementById("joinErr");

    if (pass !== document.getElementById("joinPassConf").value) {
      error.innerHTML = "Passwords don't match";
    } else {
      const body = {
        username: document.getElementById("joinUsername").value,
        password: pass,
        email: document.getElementById("joinEmail").value,
      };
      const { data } = await axios.post(
        process.env.DBAPI_URI + "/users/create",
        body
      );
      if (data.error) {
        error.innerHTML = error;
      } else {
        //Set in cache then
        navigate("/Rank");
      }
    }
  }

  return (
    <div className="flex flex-col justify-center items-center h-screen bg-slate-950">
      <div class="w-full text-left p-8">
        <h1 className="text-4xl font-bold text-white text-purple-300">
          ech<span className="text-teal-300">elo</span>n
        </h1>
      </div>
      <div className="w-1/2 h-full text-center bg-slate-800">
        <h1 className="text-4xl font-bold text-white my-2 mb-10 text-center">
          Join the <span className="text-teal-300">Race</span>.
        </h1>

        <input
          id="joinUsername"
          name="joinUsername"
          placeholder="Username"
          className="p-2 border-2 rounded border-white bg-white text-slate-800 mb-4 font-bold outline-none w-1/2"
        ></input>
        <br />
        <input
          id="joinEmail"
          name="joinEmail"
          placeholder="Email"
          className="p-2 border-2 rounded border-white bg-white text-slate-800 mb-4 font-bold outline-none w-1/2"
        ></input>
        <br />

        <input
          id="joinPass"
          name="joinPass"
          placeholder="Password"
          className="p-2 border-2 rounded border-white bg-white text-slate-800 mb-4 font-bold outline-none w-1/2"
        ></input>
        <br />

        <input
          id="joinPassConf"
          name="joinPassConf"
          placeholder="Confirm Password"
          className="p-2 border-2 rounded border-white bg-white text-slate-800 mb-4 font-bold outline-none w-1/2"
        ></input>
        <br />

        <button className="mt-5 p-2 m-2 bg-purple-500 text-purple-100 font-bold rounded hover:bg-white hover:text-purple-500">
          Create an Account
        </button>

        <p className="text-red" id="joinErr"></p>
      </div>

      {/* <div className="w-1/2 flex flex-col justify-center items-center h-full bg-blue-500">
        <h1 className="text-4xl font-bold text-purple-400 my-2">
          Welcome Back.
        </h1>

        <input
          id="loginUsername"
          name="loginUsername"
          placeholder="Username"
          className="p-2 border-b-4 border-white bg-black mb-4 font-bold outline-none"
        ></input>

        <input
          id="loginPass"
          name="loginPass"
          placeholder="Password"
          className="p-2 border-b-4 border-white bg-black mb-4 font-bold outline-none"
        ></input>

        <button className="mt-5 p-2 m-2 bg-slate-100 text-purple-500 font-bold rounded hover:bg-purple-500 hover:text-white">
          Sign In
        </button>

        <p className="text-red" id="loginErr"></p>
      </div> */}
    </div>
  );
}
